sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {


	// If the newValue is "" then don't need to update the what needed options
	// Typically part of data being loaded
	if (newValue === '') {
		return;
	}

	// Retrieve existing what needed value before clearing the options list
	var whatneeded = g_form.getValue('u_what_needed');

	//clear all choices from what needed field
	g_form.clearOptions('u_what_needed');

	// Set up valid hr options
	if(newValue == 'hr'){
		g_form.addOption('u_what_needed', 'hr1', 'Human Resources 1');
		g_form.addOption('u_what_needed', 'hr2', 'Human Resources 2');
		g_form.addOption('u_what_needed', 'other', 'Other');
	}

	// Set up valid facilities options
	if(newValue == "facilities"){
		g_form.addOption('u_what_needed', 'facilities1', 'Facilities 1');
		g_form.addOption('u_what_needed', 'facilities2', 'Facilities 2');
		g_form.addOption('u_what_needed', 'other', 'Other');
	}

	//Set up valid legal options
	if(newValue == 'legal'){
		g_form.addOption('u_what_needed', 'legal1', 'Legal 1');
		g_form.addOption('u_what_needed', 'legal2', 'Legal 2');
		g_form.addOption('u_what_needed', 'other', 'Other');
	}

	//Set up valid finance options
	if(newValue == 'finance'){
		g_form.addOption('u_what_needed', 'finance1', 'Finance 1');
		g_form.addOption('u_what_needed', 'finance2', 'Finance 2');
		g_form.addOption('u_what_needed', 'other', 'Other');
	}

	// If the change is a result of the form loading and it is not a new record
	// Then set the whatneeded field to its original value
	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed', whatneeded);
	}

}
